import React, { Component } from 'react'
import { Redirect } from 'react-router-dom'
import './Login.css';

export default class Login extends Component {
    constructor(props) {
        super(props)
        const token = localStorage.getItem("token")

        let loggedIn = true
        if (token == null) {
            loggedIn = false
        }
        this.state = {
            username: '',
            password: '',
            loggedIn
        }

        this.onChange = this.onChange.bind(this)
        this.submitForm = this.submitForm.bind(this)
    }

    onChange(e) {
        this.setState({
            [e.target.name]: e.target.value
        });
    }

    submitForm(e) {
        e.preventDefault();
        const { username, password } = this.state;
        // VALIDO EL NOMBRE DE USUARIO Y EL PASSWORD
        if (username === "lasoga" && password === "maciucci") {
            localStorage.setItem("token", "faslfhasopfjaposf");
            this.setState({
                loggedIn: true
            })
        }
    }

    render() {
        // VERIFICO SI EL USUARIO SE LOGUEO CORRECTAMENTE Y REDIRIJO AL PANEL DE ADMIN
        if (this.state.loggedIn) {
            return <Redirect to="/admin" />
        }
        return (
            // <div>
            //     <h1>Iniciar sesión</h1>
            //     <form onSubmit={this.submitForm}>
            //         <input type="text" placeholder="username" name="username" value={this.state.username} onChange={this.onChange} />
            //         <br />
            //         <input type="password" placeholder="password" name="password" value={this.state.password} onChange={this.onChange} />
            //         <br />
            //         <input type="submit" />
            //     </form>
            // </div>
            <div className="center">
                <h1>Iniciar sesión</h1>
                <form>
                    <div className="txt_field">
                        <input name="username" type="text" required />
                        <span></span>
                        <label>Nombre de usuario</label>
                    </div>
                    <div className="txt_field">
                        <input name="password" type="text" required />
                        <span></span>
                        <label>Contraseña</label>
                    </div>
                    <div className="pass">Olvidaste tu contraseña?</div>
                    <button type="submit">Iniciar sesión</button>
                    <div className="signup_link">
                        <p>No tienes una cuenta? <a href="/#">Signup</a></p>
                    </div>
                </form>
            </div>
        )
    }
}
